import { useOutletContext, useNavigate } from "react-router-dom";

import { useState } from "react";

import axios from "axios";

const BlogContent = () => {

  const [editBlog, setNewBlog] = useState({

    title: "",

    text: "",

    author: "",

    year: "",

    categories: [],

  });

  const serverData = useOutletContext();

  const navigate = useNavigate();

  const handleChange = (e) => {
    e.preventDefault();

    const { name, value } = e.target;
    setNewBlog((blog) => {
      return {
        ...blog,
        [name]: value,
      };
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    axios
      .post(`${serverData}/create-new-blog`, editBlog)
      .then((res) => {
        setNewBlog(res.data.blog);
        navigate("/blog-new-added");
      })
      .catch((err) => {
        console.log(err);
      });
  };

if(!blog){
  return
  <div>
    searching...
  </div>
}
  return (
    <div className="blog-content">

      <div className="blog-form-area">

        <br />

        <h1>Add Blog</h1>

        <br />

        <form onSubmit={handleSubmit} className="edit-form">

          <label htmlFor="title">Blog title:</label>

          <br />

          <input type="text"name="title"value={blog.title}onChange={handleChange}placeholder="Add title"/>

          <br />

          <label htmlFor="text">Blog text:</label>

          <br />

          <textarea name="text" value={blog.text} onChange={handleChange}placeholder="Add Info" ></textarea>

          <br />

          <input type="text" name="year" value={blog.year} onChange={handleChange}placeholder="Enter the Year"/>

          <br />

          <label htmlFor="author">Blog author:</label>

          <br />

          <input type="text" name="author" value={blog.author} onChange={handleChange}placeholder="Enter the Author" />

          <br />

          <label htmlFor="categories">Blog Categories:</label>

         <br />

          <input type="text" name="categories" value={blog.categories} onChange={handleChange} placeholder="Enter Category"/>

          <br />

          <label htmlFor="year">Blog Year:</label>

          <br />

          <button type='submit'>Add Blog</button>

        </form>

      </div>

    </div>
  );
};

export default BlogContent;
